import React, { useState } from "react";
import axios from "axios";


const Update = () => {


  const [formData, setFormData] = useState({
    name: "",
    satScore: "", // Rename the field to match the backend
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log("Form Data with Updated SAT Score:", formData);

    // Send a PUT request to your Spring Boot backend
    axios
      .put(`http://localhost:8080/api/sat-results/update/${formData.name}`, {
        newScore: parseFloat(formData.satScore),
      })
      .then((response) => {
        console.log("SAT Score updated successfully:", response.data);
        // You can reset the form or show a success message here
      })
      .catch((error) => {
        console.error("Error updating SAT Score:", error);
      });
  };
  return (
    

    <div className="container">
    <div className="center">
        <h1>Update</h1>
        <form method="POST" action="">
            <div className="txt_field">
                <input type="text" name="name" required></input>
                <span></span>
                <label>Name</label>
            </div>
            <div className="txt_field">
                  <input type="text" name="sat" required max="1600"></input>
                  <span></span>
                  <label>SAT Score</label>
              </div>
              <input name="submit" type="Submit" value="Update"></input>
            </form>
            </div>
            </div>


  );
};

export default Update;
